    public static Integer[] getRandomArray()
    {
        Integer[] randomData = new Integer[arrayLength];

        for (int i = 0; i < randomData.length; i++)
        {
            randomData[i] = (int) (Math.random() * 10000);
        }

        return randomData;
    }

    public static Integer[] getSortedArray()
    {
        Integer[] sortedArray = new Integer[arrayLength];

        for (int i = 0; i < sortedArray.length; i++)
        {
            sortedArray[i] = i;
        }

        return sortedArray;
    }

    public static <T> void printArray(T[] arr, String msg)
    {
        if (!msg.isEmpty())
        {
            System.out.println(msg);
        }

        for (T element : arr)
        {
            System.out.print(element + " ");
        }
        System.out.println();
    }

